/* 波浪css */
.main-hero-waves-area {
    width: 100%;
    position: absolute;
    left: 0;
    bottom: -11px;
    z-index: 5;
}

.waves-area .waves-svg {
    width: 100%;
    height: 5rem;
}

/* Animation */

.parallax>use {
    animation: move-forever 25s cubic-bezier(0.55, 0.5, 0.45, 0.5) infinite;
}

.parallax>use:nth-child(1) {
    animation-delay: -2s;
    animation-duration: 7s;
    fill: #f7f9febd;
}

.parallax>use:nth-child(2) {
    animation-delay: -3s;
    animation-duration: 10s;
    fill: #f7f9fe82;
}

.parallax>use:nth-child(3) {
    animation-delay: -4s;
    animation-duration: 13s;
    fill: #f7f9fe36;
}

.parallax>use:nth-child(4) {
    animation-delay: -5s;
    animation-duration: 20s;
    fill: #f7f9fe;
}

/* 黑色模式背景 */
[data-theme="dark"] .parallax>use:nth-child(1) {
    animation-delay: -2s;
    animation-duration: 7s;
    fill: #18171dc8;
}

[data-theme="dark"] .parallax>use:nth-child(2) {
    animation-delay: -3s;
    animation-duration: 10s;
    fill: #18171d80;
}

[data-theme="dark"] .parallax>use:nth-child(3) {
    animation-delay: -4s;
    animation-duration: 13s;
    fill: #18171d3e;
}

[data-theme="dark"] .parallax>use:nth-child(4) {
    animation-delay: -5s;
    animation-duration: 20s;
    fill: #18171d;
}

@keyframes move-forever {
    0% {
        transform: translate3d(-90px, 0, 0);
    }

    100% {
        transform: translate3d(85px, 0, 0);
    }
}

/*Shrinking for mobile*/
@media (max-width: 768px) {
    .waves-area .waves-svg {
        height: 40px;
        min-height: 40px;
    }
}

:root{
  --heo-white: #fff;
  --heo-black: #000;
  --heo-none: rgba(0,0,0,0);
  --heo-gray: #999999;
  --heo-yellow: #ffc93e;
  --heo-main: var(--heo-theme);
  --heo-main-op: var(--heo-theme-op);
  --heo-shadow-theme: 0 8px 12px -3px var(--heo-theme-op);
  --heo-shadow-main: 0 8px 12px -3px var(--heo-main-op);
  --heo-shadow-blue: 0 8px 12px -3px rgba(40, 109, 234,.20);
  --heo-shadow-white: 0 8px 12px -3px rgba(255, 255, 255,.20);
  --heo-shadow-black: 0 0 12px 4px rgba(0, 0, 0,.05);
  --heo-shadow-yellow: 0px 38px 77px -26px rgba(255, 201, 62,.12);
  --heo-shadow-red: 0 8px 12px -3px #ee7d7936;
  --heo-shadow-green: 0 8px 12px -3px #87ee7936;
  --heo-logo-color: linear-gradient(215deg,#4584ff 30%,#ff7676 70%);
  --style-border: 1px solid var(--heo-card-border);
  --style-border-hover: 1px solid var(--heo-theme);
}

::selection {
  background: var(--heo-fontcolor);
  color: var(--heo-background);
}

[data-theme=light] {
  --heo-theme: #425AEF;
  --heo-theme-op: #4259ef23;
  --heo-blue: #425AEF;
  --heo-red: #D8213C;
  --heo-pink: #FF7C7C;
  --heo-green: #57bd6a;
  --heo-fontcolor: #363636;
  --heo-background: #f7f9fe;
  --heo-reverse: #000;
  --heo-maskbg: rgba(255, 255, 255, 0.6);
  --heo-maskbgdeep: rgba(255, 255, 255, 0.85);
  --heo-hovertext: var(--heo-theme);
  --heo-ahoverbg: #F7F7FA;
  --heo-lighttext: var(--heo-main);
  --heo-secondtext: rgba(60, 60, 67, 0.6);
  --heo-scrollbar: rgba(60, 60, 67, 0.4);
  --heo-card-btn-bg: #edf0f7;
  --heo-post-blockquote-bg: #fafcff;
  --heo-post-tabs-bg: #f2f5f8;
  --heo-secondbg: #edf0f7;
  --heo-shadow-nav:0 5px 12px -5px rgba(102, 68, 68, 0.05);
  --heo-card-bg: #fff;
  --heo-shadow-lightblack:0 5px 12px -5px rgba(102, 68, 68, 0.00);
  --heo-shadow-light2black:0 5px 12px -5px rgba(102, 68, 68, 0.00);
  --heo-card-border: #e3e8f7;
}

[data-theme=dark] {
  --heo-theme: #0084FF;
  --heo-theme-op: #0084FF23;
  --heo-blue: #0084FF;
  --heo-red: #FF3842;
  --heo-pink: #FF7C7C;
  --heo-green: #57bd6a;
  --heo-fontcolor: #F7F7FA;
  --heo-background: #18171d;
  --heo-reverse: #fff;
  --heo-maskbg: rgba(0,0,0, 0.6);
  --heo-maskbgdeep: rgba(0,0,0, 0.85);
  --heo-hovertext: #0A84FF;
  --heo-ahoverbg: #fff;
  --heo-lighttext: #f2b94b;
  --heo-secondtext: #a1a2b8;
  --heo-scrollbar: rgba(200, 200, 223, 0.4);
  --heo-card-btn-bg: #30343f;
  --heo-post-blockquote-bg: #000;
  --heo-post-tabs-bg: #121212;
  --heo-secondbg: #30343f;
  --heo-shadow-nav:0 5px 20px 0px rgba(28, 28, 28, 0.4);
  --heo-card-bg: #1d1b26;
  --heo-shadow-lightblack:0 5px 12px -5px rgba(102, 68, 68, 0.0);
  --heo-shadow-light2black:0 5px 12px -5px rgba(102, 68, 68, 0.0);
  --heo-card-border: #42444a;
}

@media screen and (max-width: 768px){
  :root{
    --style-border: 0px solid var(--heo-card-border);
    --style-border-hover: 0px solid var(--heo-theme);
  }
}

/* 基础样式 */
:root {
  --primary-color: #8a63d2;
  --secondary-color: #5e8af7;
  --text-primary: #2c3e50;
  --text-secondary: #5d6d7c;
  --text-contrast: #fff;
  --glass-bg: rgba(255,255,255,0.92);
  --glass-border: rgba(255,255,255,0.75);
  --glass-shadow: 0 15px 50px rgba(0,0,0,0.12);
  --card-radius: 16px;
  --transition: all 0.35s cubic-bezier(0.165, 0.84, 0.44, 1);
  --module-spacing: 3rem;
  --blur-intensity: 12px;
}
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
body {
  font-family: 'Inter', 'PingFang SC', 'Microsoft YaHei', sans-serif;
  background: linear-gradient(135deg, #f5f7ff 0%, #eef1f8 100%);
  color: var(--text-primary);
  line-height: 1.6;
  min-height: 100vh;
  padding: 0;
  margin: 0;
  overflow-x: hidden;
}
/* 容器样式 */
#docs-page {
  max-width: 1400px;
  margin: 0 auto;
  padding: 2rem 1.5rem;
}
.docs-container {
  display: flex;
  flex-direction: column;
  gap: var(--module-spacing);
}
/* 头部图像区域 */
.header-image {
  background: linear-gradient(135deg, #6a11cb 0%, #2575fc 100%);
  height: 220px;
  border-radius: var(--card-radius);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  box-shadow: var(--glass-shadow);
}
.header-image::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: url("data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="none"><path d="M0,0 L100,0 L100,100 Q50,80 0,100 Z" fill="rgba(255,255,255,0.15)"/></svg>");
  background-size: 100% 100%;
}
.header-content {
  position: relative;
  z-index: 2;
  text-align: center;
  padding: 0 1.5rem;
}
.docs-title {
  font-size: 2.8rem;
  font-weight: 800;
  margin-bottom: 1rem;
  color: #fff;
  text-shadow: 0 2px 10px rgba(0,0,0,0.2);
}
.docs-description {
  font-size: 1.3rem;
  color: rgba(255,255,255,0.9);
  max-width: 700px;
  margin: 0 auto;
}
/* 搜索区域 */
.search-container {
  background: var(--glass-bg);
  backdrop-filter: blur(var(--blur-intensity));
  -webkit-backdrop-filter: blur(var(--blur-intensity));
  border: 1px solid var(--glass-border);
  border-radius: var(--card-radius);
  box-shadow: var(--glass-shadow);
  padding: 1.8rem 2rem;
  margin-bottom: 1.5rem;
  position: relative;
}
.docs-search-container {
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
  position: relative;
}
.search-box {
  display: flex;
  background: #fff;
  border-radius: 50px;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.08);
  transition: var(--transition);
}
.search-box:hover,
.search-box:focus-within {
  box-shadow: 0 15px 50px rgba(0,0,0,0.15);
  transform: translateY(-3px);
}
.search-box input {
  flex: 1;
  border: none;
  padding: 1.4rem 1.5rem;
  font-size: 1.15rem;
  font-weight: 500;
  color: var(--text-primary);
  background: rgba(255,255,255,0.95);
  transition: var(--transition);
}
.search-box input::placeholder {
  color: #a0a0a0;
  transition: all 0.3s;
}
.search-box input:focus {
  outline: none;
  background: #fff;
}
.search-box input:focus::placeholder {
  opacity: 0.5;
}
.search-btn {
  background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
  color: #fff;
  border: none;
  padding: 0 2.2rem;
  cursor: pointer;
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 0.8rem;
  transition: var(--transition);
  font-size: 1.1rem;
}
.search-btn:hover {
  filter: brightness(1.08);
  padding: 0 2rem 0 2.4rem;
}
/* 功能模块布局 */
.feature-modules {
  margin-top: 1rem;
}
.module-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2.2rem;
}
/* 整体模块容器 */
.module-container {
  display: flex;
  flex-direction: column;
  height: 100%;
  border-radius: var(--card-radius);
  transition: var(--transition);
  overflow: hidden;
}
.module-container:hover {
  transform: translateY(-10px);
  box-shadow: 0 25px 60px rgba(0,0,0,0.18);
}
/* 模块内容区 */
.module-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 2rem;
  background: var(--glass-bg);
  backdrop-filter: blur(var(--blur-intensity));
  -webkit-backdrop-filter: blur(var(--blur-intensity));
  position: relative;
  border-radius: var(--card-radius);
}
/* 功能卡片样式 */
.feature-card {
  display: flex;
  flex-direction: column;
  height: 100%;
  text-align: center;
}
.card-icon {
  width: 85px;
  height: 85px;
  border-radius: 50%;
  margin: 0 auto 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, rgba(138,99,210,0.15), rgba(94,138,247,0.15));
}
.card-icon i {
  font-size: 2.5rem;
  background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}
.card-title {
  font-size: 1.7rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
}
.card-subtitle {
  color: var(--secondary-color);
  font-weight: 600;
  font-size: 1.1rem;
  margin-bottom: 1.5rem;
  line-height: 1.5;
}
.section-divider {
  width: 100%;
  height: 4px;
  background: linear-gradient(to right, var(--primary-color), var(--secondary-color));
  border-radius: 2px;
  margin: 0 auto 1.8rem;
}
.card-content {
  flex: 1;
  margin-bottom: 1.5rem;
  padding: 0 1.2rem;
}
.feature-list {
  list-style: none;
  padding: 0;
  margin: 0;
  text-align: center;
}
.feature-list li {
  color: var(--text-secondary);
  margin-bottom: 1.1rem;
  padding: 0.5rem 0;
  position: relative;
  font-size: 1.05rem;
  line-height: 1.6;
  transition: var(--transition);
}
.feature-list li:before {
  content: '•';
  color: var(--primary-color);
  font-weight: bold;
  display: inline-block;
  width: 1em;
  margin-left: -1em;
}
.feature-list li:hover {
  color: var(--primary-color);
  transform: translateX(5px);
}
.card-footer {
  margin-top: auto;
  text-align: center;
  padding-top: 1.5rem;
}
.action-btn {
  display: inline-flex;
  align-items: center;
  gap: 0.8rem;
  padding: 0.8rem 1.8rem;
  background: linear-gradient(135deg, var(--primary-color), var(--secondary-color));
  color: #fff;
  border-radius: 30px;
  text-decoration: none;
  font-weight: 600;
  font-size: 1.05rem;
  transition: var(--transition);
  box-shadow: 0 5px 20px rgba(138,99,210,0.25);
}
.action-btn:hover {
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(138,99,210,0.35);
  gap: 1rem;
}
/* 响应式设计 */
@media (max-width: 1200px) {
  .module-grid {
    gap: 1.8rem;
  }
}
@media (max-width: 992px) {
  .module-grid {
    grid-template-columns: 1fr;
    max-width: 700px;
    margin: 0 auto;
    gap: 3rem;
  }
  .module-container {
    margin-bottom: 1rem;
  }
}
@media (max-width: 768px) {
  #docs-page {
    padding: 1.5rem;
  }
  .docs-title {
    font-size: 2.3rem;
  }
  .docs-description {
    font-size: 1.15rem;
  }
  .header-image {
    height: 180px;
  }
  .search-box input {
    padding: 1.2rem 1.5rem;
    font-size: 1rem;
  }
  .search-btn {
    padding: 0 1.5rem;
  }
  .search-btn span {
    display: none;
  }
  .module-content {
    padding: 1.5rem;
  }
}
@media (max-width: 480px) {
  .docs-title {
    font-size: 2rem;
  }
  .docs-description {
    font-size: 1.1rem;
  }
  .header-image {
    height: 160px;
    padding: 1.5rem;
  }
  .search-box input {
    padding: 1.1rem 1.2rem;
  }
  .module-content {
    padding: 1.2rem;
  }
  .card-icon {
    width: 75px;
    height: 75px;
  }
  .card-icon i {
    font-size: 2rem;
  }
}

document.addEventListener('DOMContentLoaded', function() {
  const searchInput = document.getElementById('docs-search-input');
  const searchBtn = document.querySelector('.search-btn');

  // 自动聚焦搜索框
  setTimeout(() => {
    if(searchInput) searchInput.focus();
  }, 500);

  if (searchBtn && searchInput) {
    searchBtn.addEventListener('click', function() {
      const query = searchInput.value.trim();
      if (query) {
        const originalHTML = this.innerHTML;
        this.innerHTML = '<i class="fas fa-spinner fa-spin"></i> 搜索中...';
        this.disabled = true;

        setTimeout(() => {
          this.innerHTML = originalHTML;
          this.disabled = false;
          alert(`执行搜索: ${query}`);
        }, 800);
      }
    });

    searchInput.addEventListener('keypress', function(e) {
      if (e.key === 'Enter') {
        searchBtn.click();
      }
    });
  }

  // 卡片悬停动画
  const modules = document.querySelectorAll('.module-container');
  modules.forEach(module => {
    module.addEventListener('mouseenter', function() {
      this.style.transform = 'translateY(-10px)';
      this.style.boxShadow = '0 25px 60px rgba(0, 0, 0, 0.18)';
    });

    module.addEventListener('mouseleave', function() {
      this.style.transform = '';
      this.style.boxShadow = '';
    });
  });

  // 列表项悬停效果
  const listItems = document.querySelectorAll('.feature-list li');
  listItems.forEach(item => {
    item.addEventListener('mouseenter', function() {
      this.style.transform = 'translateX(5px)';
      this.style.color = 'var(--primary-color)';
    });

    item.addEventListener('mouseleave', function() {
      this.style.transform = '';
      this.style.color = '';
    });
  });
});

document.addEventListener('DOMContentLoaded', function () {
    // 为时间线项目添加悬停效果
    document.querySelectorAll('.timeline-content').forEach(item => {
        item.addEventListener('mouseenter', function () {
            this.style.transform = 'translateY(-8px)';
            this.style.boxShadow = '0 15px 35px rgba(31, 38, 135, 0.25)';
        });

        item.addEventListener('mouseleave', function () {
            this.style.transform = 'translateY(0)';

            // 根据卡片位置恢复不同阴影效果
            const isEven = this.closest('.timeline-item').classList.contains('even');
            if (isEven) {
                this.style.boxShadow = '0 8px 32px rgba(31, 38, 135, 0.15), 0 0 15px rgba(74, 159, 245, 0.2)';
            } else {
                this.style.boxShadow = '0 8px 32px rgba(31, 38, 135, 0.15), 0 0 15px rgba(255, 126, 179, 0.2)';
            }
        });
    });

    // 标签悬停效果
    document.querySelectorAll('.tag').forEach(tag => {
        tag.addEventListener('mouseenter', function () {
            this.style.transform = 'scale(1.05)';
        });

        tag.addEventListener('mouseleave', function () {
            this.style.transform = 'scale(1)';
        });
    });

    // 心电图动画效果增强
    const timeline = document.querySelector('.timeline');

    // 只在桌面端执行心电图动画
    if (window.innerWidth > 768 && timeline) {
        setInterval(() => {
            const pulse = document.createElement('div');
            pulse.style.position = 'absolute';
            pulse.style.left = '50%';
            pulse.style.top = '0';
            pulse.style.width = '3px';
            pulse.style.height = '20px';
            pulse.style.background = '#ff7eb3';
            pulse.style.transform = 'translateX(-50%)';
            pulse.style.zIndex = '1';
            pulse.style.boxShadow = '0 0 10px rgba(255, 126, 179, 0.5)';
            pulse.style.animation = 'heartbeat 1.8s forwards';
            timeline.appendChild(pulse);

            setTimeout(() => {
                pulse.remove();
            }, 1800);
        }, 2000);
    }
});

/* ====== 每日诗词组件样式 ====== */

/* 基础变量定义 */
:root {
  --poem-primary: #8a63d2;
  --poem-secondary: #5e8af7;
  --poem-accent: #ff7eb3;
  --poem-text-primary: #2c3e50;
  --poem-text-secondary: #6a7ba2;
  --poem-bg-primary: rgba(255, 255, 255, 0.95);
  --poem-bg-secondary: rgba(138, 99, 210, 0.1);
  --poem-border: rgba(138, 99, 210, 0.2);
  --poem-shadow: 0 8px 32px rgba(31, 38, 135, 0.15);
  --poem-shadow-hover: 0 12px 40px rgba(31, 38, 135, 0.25);
  --poem-radius: 16px;
  --poem-transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* 深色模式适配 */
[data-theme="dark"] {
  --poem-primary: #a78bfa;
  --poem-secondary: #7c9eff;
  --poem-accent: #ff9ecd;
  --poem-text-primary: #f7f7fa;
  --poem-text-secondary: #a1a2b8;
  --poem-bg-primary: rgba(29, 30, 34, 0.95);
  --poem-bg-secondary: rgba(167, 139, 250, 0.15);
  --poem-border: rgba(167, 139, 250, 0.3);
}

/* 深色模式下的主容器样式 */
[data-theme="dark"] #card-poem.card-widget {
  background: linear-gradient(135deg, rgba(29, 30, 34, 0.65) 60%, rgba(167, 139, 250, 0.13) 100%);
  border: 1.5px solid rgba(167, 139, 250, 0.25);
}

[data-theme="dark"] #card-poem.card-widget::before {
  background: linear-gradient(120deg, rgba(167, 139, 250, 0.15) 0%, rgba(167, 139, 250, 0.03) 100%);
}

[data-theme="dark"] #card-poem.card-widget:hover::before {
  background: linear-gradient(120deg, rgba(167, 139, 250, 0.25) 0%, rgba(167, 139, 250, 0.05) 100%);
}

[data-theme="dark"] #poem_sentence {
  background: rgba(29, 30, 34, 0.4);
  border: 1px solid rgba(167, 139, 250, 0.2);
}

/* 主容器样式 - 高级拟态毛玻璃效果 */
#card-poem.card-widget {
  position: relative;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.65) 60%, rgba(138, 99, 210, 0.13) 100%);
  border-radius: 28px;
  box-shadow:
    0 8px 32px 0 rgba(31, 38, 135, 0.18),
    0 1.5px 4px 0 rgba(138, 99, 210, 0.10),
    0 0.5px 0.5px 0 rgba(255, 255, 255, 0.25) inset;
  backdrop-filter: blur(24px) saturate(1.8);
  -webkit-backdrop-filter: blur(24px) saturate(1.8);
  border: 1.5px solid rgba(138, 99, 210, 0.18);
  padding: 2.2rem 1.4rem 1.4rem 1.4rem;
  margin-bottom: 1.5rem;
  transition: box-shadow 0.3s, transform 0.3s;
  overflow: hidden;
}

/* 悬停效果 - 高级拟态动效 */
#card-poem.card-widget:hover {
  transform: translateY(-6px) scale(1.025);
  box-shadow:
    0 16px 48px 0 rgba(31, 38, 135, 0.22),
    0 3px 12px 0 rgba(138, 99, 210, 0.13);
  border-color: var(--poem-primary);
}

/* 装饰性背景 - 高级高光效果 */
#card-poem.card-widget::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
